mapper.select:
SELECT

FROM

WHERE
tenantId = #{tenantId}
AND applicationName = #{applicationName}

    AND "timestamp" BETWEEN #{range.from} AND #{range.to}


    AND agentId = #{agentId}


    AND transactionId = #{transactionId}
    AND spanId = #{spanId}
    AND exceptionId = #{exceptionId}

ORDER BY "timestamp" asc, exceptionDepth asc
LIMIT ${limit}

SELECT

FROM

WHERE
tenantId = #{tenantId}
AND applicationName = #{applicationName}

    AND "timestamp" BETWEEN #{range.from} AND #{range.to}


    AND agentId = #{agentId}


    AND transactionId = #{transactionId}
    AND spanId = #{spanId}
    AND exceptionId = #{exceptionId}

ORDER BY ${orderBy.getAttributeName} ${isDesc}
LIMIT ${limit}

SELECT

FROM

WHERE
tenantId = #{tenantId}
AND applicationName = #{applicationName}

    AND agentId = #{agentId}


    AND transactionId = #{transactionId}
    AND spanId = #{spanId}
    AND exceptionId = #{exceptionId}
    AND exceptionDepth = #{exceptionDepth}

ORDER BY "timestamp" asc
LIMIT ${limit}

SELECT

arraySliceInt(
HISTOGRAM("timestamp", #{range.from}, #{range.to}, #{timeWindowRangeCount}), 0, #{timeWindowRangeCount}
) as "values"

FROM

WHERE
tenantId = #{tenantId}
AND applicationName = #{applicationName}
AND "timestamp" BETWEEN #{range.from} AND #{range.to}

    AND agentId = #{agentId}

GROUP BY applicationName

ORDER BY count(*) desc
LIMIT 10
